import React from 'react';
import ReactDOM from 'react-dom';

var animes = { name: 'Black Clover', type: 'Shonnen', studio: 'Pierrot'};
  // { name: 'Shingeki no Kyojin', type: 'Shonnen-seinen', studio: 'WIT - MAPPA'}
// const App = <h1>A ver:{prueba(element.name,element.type)}</h1>;
const Root = document.getElementById('root');

// function prueba(name,type){
//   return "El anime "+name+", es del genero "+type;
// }

// ReactDOM.render(App,Root);

//componentes
// const TarjetaAnime = (props) =>(
//   <div>
//     <h1>Title:{props.name}</h1>
//     <hr></hr>
//     <h1>Type:{props.type}</h1>
//     <hr></hr>
//     <h1>Studio:{props.studio}</h1>
//   </div>
// );

// clases

class TarjetaAnime extends React.Component{
  constructor () {
    super()

    const METHODS = [ 
      'agregar',
      'quitar',
      'limpiar'
    ]

    METHODS.forEach((method) => { // linea para evitar error con el this
      this[method] = this[method].bind(this)
    })

    this.state = {
      calificacion: 0
    }
  }

  agregar () {
    this.setState({ // funcion reservada para actualizar el contador en tiempo real
      calificacion: this.state.calificacion + 1
    })
  }

  quitar () {
    this.setState({
      calificacion: this.state.calificacion - 1
    })
  }

  limpiar () {
    this.setState({
      calificacion: 0
    })
  }

  render(){
    return(
      <div>
        <h1>Title:{props.name}</h1>
        <hr></hr>
        <h1>Type:{props.type}</h1>
        <hr></hr>
        <h1>Studio:{props.studio}</h1>
        <hr></hr>
        <h1>Calificacion:{this.state.calificacion}</h1>
        <button onClick={this.agregar} > + </button>
        <button onClick={this.quitar} > - </button>
        <button onClick={this.limpiar} > Limpiar</button> 
      </div>
    )
  }
}

const App = () => (
  <div>
      <TarjetaAnime name={animes.name} type={animes.type} studio={animes.studio}/>
  </div>
);

ReactDOM.render(<App/>,Root);
