import React from 'react';
import ReactDOM from 'react-dom';
import TarjetaAnime from './components/TarjetaAnime';

var animes = { name: 'Black Clover', type: 'Shonnen', studio: 'Pierrot'};
  // { name: 'Shingeki no Kyojin', type: 'Shonnen-seinen', studio: 'WIT - MAPPA'}
// const App = <h1>A ver:{prueba(element.name,element.type)}</h1>;

class App extends React.Component{
  constructor () {
    super()

    this.state = {
      adding: false,
      name: '',
      type: '',
      studio: ''
    }

    const METHODS = [ 
      'Add'
    ]

    METHODS.forEach((method) => { // linea para evitar error con el this
      this[method] = this[method].bind(this)
    })

    
  }

  handleSubmit = (event) => {
    event.preventDefault() // evento para que no se recargue la pagina automaticamente con el form

    this.setState({ 
      adding: true,
      name: event.target[0].value,
      type: event.target[1].value,
      studio: event.target[2].value
    })
  }

  render(){
    var vali = "";
    if (this.state.adding) {
      vali = <TarjetaAnime name={this.state.name} type={this.state.type} studio={this.state.studio} />
      this.state.adding = false
    }
    return(
      <div className={'prueba'}>
        <form onSubmit={this.handleSubmit}>
          <input
            type="text"
            placeholder='Nombre'
          />
          <input
            type="text"
            placeholder='Género'
          />
          <input
            type="text"
            placeholder='Estudio de animación'
          />
          <button>Agregar Contenedor</button>
        </form>
        <TarjetaAnime name={animes.name} type={animes.type} studio={animes.studio} />
        {vali}
      </div>
    )
  }
}


export default App
